    public void resultbox( int x1,  int y1) {
        /*Dialog dialog = new Dialog(MainActivity.this);*/
        Dialog dialog = new Dialog(this, android.R.style.Theme_Light);
        dialog.requestWindowFeature(Window.FEATURE_NO_TITLE);
        dialog.setContentView(R.layout.activity_result);
        Button pla1 = (Button) dialog.findViewById(R.id.pla1);
        Button pla2 = (Button) dialog.findViewById(R.id.pla2);
        TextView win1 = (TextView) dialog.findViewById(R.id.win1);
        TextView win2 = (TextView) dialog.findViewById(R.id.win2);
        TextView msg = (TextView) dialog.findViewById(R.id.msg);
        pla2.setText(pl2 + " WINS");
        pla1.setText(pl1 + " WINS");

        final MediaPlayer mp1 = MediaPlayer.create(this, R.raw.sound);
        EditText p1w = (EditText) dialog.findViewById(R.id.p1w);
        EditText p2w = (EditText) dialog.findViewById(R.id.p2w);
        p1w.setText("" + x1);
        p2w.setText("" + y1);
        w1 = x1;
        w2 = y1;
        if (!computer) {
            if ((w1 - w2) >= 3 && (w1 - w2) < 5) {
                msg.setText(pl1 + " IS DOMINATING");
            }
            if ((w2 - w1) >= 3 && (w2 - w1) < 5) {
                msg.setText(pl2 + " IS DOMINATING");
            }
            if ((w1 - w2) >= 5 && (w1 - w2) < 7) {
                msg.setText(pl1 + " IS UNSTOPPABLE");
            }
            if ((w2 - w1) >= 5 && (w1 - w2) < 7) {
                msg.setText(pl2 + " IS UNSTOPPABLE");
            }
            if ((w1 - w2) >= 7 && (w1 - w2) < 10) {
                msg.setText(pl1 + " IS GODLIKE");
            }
            if ((w2 - w1) >= 7 && (w1 - w2) < 10) {
                msg.setText(pl2 + " IS GODLIKE");
            }
            if ((w1 - w2) >= 10) {
                msg.setText(pl1 + " IS BEYOND GODLIKE");
            }
            if ((w2 - w1) >= 10) {
                msg.setText(pl2 + " IS BEYOND GODLIKE");
            }
        }
        if (round3) {
            if ((w1 + w2) == 3 || (w1 - w2) == 2 || (w2 - w1) == 2) {
                if (w1 > w2) {
                    win1.setText(R.string.Winner);
                    win1.setTextColor(Color.parseColor("#41FF24"));
                    /*win2.setText(R.string.Loser);*/
                    win2.setTextColor(Color.parseColor("#FF2121"));
                } else {
                    win2.setText(R.string.Winner);
                    win2.setTextColor(Color.parseColor("#41FF24"));
                    /*win1.setText(R.string.Loser);*/
                    win1.setTextColor(Color.parseColor("#FF2121"));
                }
            }
        }
        Button rs = (Button) dialog.findViewById(R.id.rs);
        if (round3) {
            if ((w1 + w2) != 3 && (w2 - w1) != 2 && (w1 - w2) != 2) {
                Handler mHandler = new Handler();
                mHandler.postDelayed(new Runnable() {

                    @Override
                    public void run() {
                        //start your activity here
                        mp1.start();
                        Intent intent = getIntent();
                        intent.putExtra("p1wins", w1);
                        intent.putExtra("p2wins", w2);
                        finish();
                        startActivity(intent);
                    }

                }, 1000L);
                rs.setVisibility(View.INVISIBLE);

            }
        }
        rs.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {

                Intent intent = getIntent();
                intent.putExtra("p1wins", w1);
                intent.putExtra("p2wins", w2);
                finish();
                r1 = 3;
                r2 = 5;
                r3 = 7;

                if (((w1 + w2) == 3 || (w1 - w2) == 2 || (w2 - w1) == 2) && round3) {
                    w1 = 0;
                    w2 = 0;
                    intent.putExtra("p1wins", w1);
                    intent.putExtra("p2wins", w2);
                    if (mInterstitialAd.isLoaded()) {
                        mInterstitialAd.show();
                    } else {
                        Log.d("TAG", "The interstitial wasn't loaded yet.");
                    }
                }
                startActivity(intent);

            }
        });

        dialog.show();
    }


